import java.util.Scanner;

public class Factorial {

    // Iterative method to calculate factorial
    public static long factorialIterative(int n) {
        if (n < 0) {
            throw new IllegalArgumentException("Factorial is not defined for negative numbers.");
        }
        long result = 1;
        for (int i = 1; i <= n; i++) {
            result *= i;
        }
        return result;
    }

    // Recursive method to calculate factorial
    public static long factorialRecursive(int n) {
        if (n < 0) {
            throw new IllegalArgumentException("Factorial is not defined for negative numbers.");
        }
        if (n == 0 || n == 1) {
            return 1;
        } else {
            return n * factorialRecursive(n - 1);
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Enter a number to calculate its factorial: ");
        int number = scanner.nextInt();

        // Using iterative method
        long iterativeResult = factorialIterative(number);
        System.out.println("Iterative: The factorial of " + number + " is: " + iterativeResult);

        // Using recursive method
        long recursiveResult = factorialRecursive(number);
        System.out.println("Recursive: The factorial of " + number + " is: " + recursiveResult);

        scanner.close();
    }
}

import java.util.Scanner;

public class SimpleFibonacci {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Enter the number of terms in the Fibonacci series: ");
        int number = scanner.nextInt();

        int a = 0, b = 1;

        System.out.print("Fibonacci Series: ");
        for (int i = 1; i <= number; i++) {
            System.out.print(a + " ");
            int next = a + b; // Calculate the next term
            a = b;            // Update a to the next term
            b = next;        // Update b to the next term
        }
        System.out.println(); // Move to the next line after printing the series

        scanner.close();
    }
}

